import React from "react";

import classes from "./chatAdvices.module.css"
import Advices from "./advices";
import secret from "../secrets/gptKey.txt";

const ChatAdvices = () => {
    let response = 'hello';
    const [textData, setTextData] = useState('');
    const [adviceText, setAdviceText] = useState('');
    const [userInput, setUserInput] = useState("");


    const fetchAdvice = async () => {
        const requestBody = {
            model: "gpt-3.5-turbo",
            messages: [{ role: "user", content: {userInput} }]
        };

        try {
            const response = await axios.post("https://api.openai.com/v1/chat/completions", requestBody, {
                headers: {
                    "Content-Type": "application/json",
                    "Authorization": `Bearer ${textData}`
                }
            });

            const advice = response.data.choices[0].message.content;
            setAdviceText(advice);
        } catch (error) {
            console.error("Error fetching advice:", error);
        }
    };

    useEffect(() => {
        fetch(secret)
            .then((response) => response.text())
            .then((data) => setTextData(data))
            .catch((error) => console.error('Error fetching text file:', error));
    }, []);

    const handleInputChange = (event) => {
        setUserInput(event.target.value);
    };

    return (
        <div>
            <Advices response={adviceText}>
            </Advices>

            <input
                type="text"
                value={userInput}
                onChange={handleInputChange}
                className={classes.input}
                placeholder="Enter your request"
            />

            <button className={classes.button} onClick={fetchAdvice}> Get advice! </button>
        </div>
    )
}

export default ChatAdvices;
